INSERT INTO `abouts` (`id`, `name`, `description`, `phone`, `image`, `favicon`, `image_footer`) VALUES
(1, 'Empowering Health with Innovation Since 2013.', '<p>At Aqualife, we&#39;ve been delivering cutting-edge water filtration systems since 2013, providing homes, businesses, and industries across Indonesia with the pure, safe water they deserve. Our core values of innovation, sustainability, and health drive us to offer high-performance systems backed by exceptional customer service and fast after-sales support. With over 5,000 satisfied customers, we&#39;re committed to making clean water a necessity for every home and business.</p>\r\n', '081234567', '', '', '');
INSERT INTO `about_details` (`id`, `title`, `subtitle`, `icon`) VALUES
(1, 'Premium Water Filtration Systems', 'Backed by Over 7 Years of Expertise', 'box'),
(2, 'Rated Highly for Exceptional Service', 'From Consultation to Installation', 'thumbs-up'),
(3, 'Fast, Reliable Service', 'Ensuring Your Water System Works When You Need It Most', 'alert-circle');
INSERT INTO `adventages` (`id`, `title`, `description`, `images`, `category_id`) VALUES
(1, 'Perbedaan Nyata: Sebelum & Sesudah', 'Perbedaan Nyata: Sebelum & Sesudah', 'J.png', 3),
(2, 'Use Aqualife Produk', 'Use Aqualife Produk', 'M.png', 4),
(3, 'Use Gallon', 'Use Gallon', 'M2.png', 4),
(4, 'See the Aqualife Luma Difference', '<b style=\"font-size:20px>Prevent Scale, Rust, and Odor—Protect Your Health and Appliances</b></br>Untreated water often contains minerals, iron, and other impurities that lead to scale buildup, rust, and stains on household appliances. With Aqualife CleanFlow, your water is purified leaving your faucets, showers, and fixtures clean, rust-free, and longer-lasting. Protect your family\'s health and your appliances with every drop.', 'I.jpg', 4);
INSERT INTO `banner_headers` (`id`, `title`, `subtitle`, `description`, `images`, `category_id`) VALUES
(1, 'Home', 'Pure Water.Healthier Life.With Every Drop.', 'At Aqualife, we bring the future of filtration to your home, ensuring your family enjoys pure, healthy air every day. Whether for home or industry, Aqualife is your answer to air quality.', 'home.png', 1),
(2, 'WATER TREATMENT PLANT', 'Whole House Water Filtration System', 'Aqualife Luma effectively removes contaminants such as sediments, chemicals, heavy metals, iron, manganese, and unpleasant odors providing clean, safe, and high-quality water throughout your home.', 'A.jpg', 2),
(3, 'Water Softener For Your Home', 'Goodbye Hard Water, Hello Aqualife Luma Water Softener!', 'The Aqualife Luma water softening system brings you a host of benefits, from softer skin and hair to cleaner, brighter clothes. Say goodbye to stains and scale buildup, and protect your household appliances from hard water damage, extending their lifespan.', 'ws.jpg', 3),
(4, ' Aqualife Reverse Osmosis System', 'Quality You Can Taste', 'Aqualife\'s advanced Reverse Osmosis (RO) system removes up to 99% of bacteria, viruses, chlorine, and heavy metals—delivering crystal-clear, great-tasting water on demand.', 'DW.jpg', 4),
(5, 'Trending News', 'News', 'Regarding Clean Water • Regarding Clean Water • Regarding Clean Water', 'A.png', 5);
INSERT INTO `benefits` (`id`, `category_id`, `images`, `title`, `description`) VALUES
(1, '2', 'D.png', 'Improving Water Quality', 'Eliminates unpleasant odors (such as chlorine and rotten egg smell), discoloration, and harmful chemicals, giving your water a clean, fresh taste.'),
(2, '2', 'C.png', 'Cleaner and Safer Water', 'Removes bacteria, viruses, iron, manganese, sediments, and harmful contaminants, providing safer and healthier water for your family.'),
(3, '2', 'E.jpg', 'Efficient and Cost Effective', 'Saves money by reducing your reliance on bottled water, providing a cost-effective long-term solution.'),
(4, '2', 'F.jpg', 'Reliable and Durable System', 'Built with advanced, reliable technology ensuring long-lasting performance and fewer maintenance concerns.'),
(5, '2', 'G.jpg', 'Environmentally Friendly', 'Minimizes plastic waste by eliminating the need for disposable water bottles, helping protect the environment.'),
(6, '2', 'H.png', 'Trusted Technical Support', 'Our responsive, expert after-sales support ensures your water treatment system operates flawlessly at all times.'),
(7, '3', 'D.jpg', 'Prevents Scale Buildup', 'Protects pipes, faucets, and appliances from scale buildup caused by hard water, extending their lifespan.'),
(8, '3', 'E.png', 'Softer Skin and Hair', 'Helps retain moisture in your skin and hair, reducing dryness and irritation caused by hard water.'),
(9, '3', 'F.jpg', 'Improves Detergent Efficiency', 'Soft water allows detergents to work more effectively, leading to cleaner clothes, dishes, and surfaces with less detergent.'),
(10, '3', 'G.jpg', 'Extends Appliance Lifespan', 'Prevents mineral buildup in washing machines, water heaters, and dishwashers, reducing maintenance costs and extending their lifespan.'),
(11, '3', 'H.png', 'Reduces Energy Consumption', 'Improves water heater efficiency by preventing scale buildup, which in turn reduces energy consumption and utility bills.'),
(12, '3', 'I.jpg', 'Prevents Stains on Dishes', 'Eliminates cloudy spots and streaks caused by mineral deposits in hard water, keeping your dishes and glassware sparkling clean.'),
(13, '4', 'H.png', 'Health & Hydration', 'Supports overall well-being with pure drinking water.'),
(14, '4', 'I.jpg', 'Compact, Modern Design', 'Fits seamlessly under sinks or on countertops.'),
(15, '4', 'J.jpg', 'Cost-Effective & Eco-Friendly', 'Reduces bottled-water expenses and plastic waste.'),
(16, '4', 'K.png', 'Reliable & Easy to Use', 'Simple installation and hasslefree maintenance.'),
(17, '4', 'L.png', 'Balanced pH & Rich in Minerals', 'Restores healthy calcium and magnesium for smoother taste and better hydration.'),
(18, '4', 'F.jpg', '99% Contaminant Removal', 'Eliminates bacteria, viruses, chlorine and heavy metals.'),
(19, '4', 'G.jpg', 'Better Taste & Odor-Free', 'Filters out unpleasant flavors and smells.');
